import React, { useState } from 'react';
import JavaScriptUnminifier from './components/JavaScriptUnminifier.js';
import './App.css';
import HtmlStripper from './components/htmlStripper.js';
import ReverseGSTCalculator from './components/ReverseGSTCalculator.js';

// Tab component
const Tab = ({ label, active, onClick }) => {
  return (
    <button className={active ? "active" : ""} onClick={onClick}>
      {label}
    </button>
  );
};

function App() {
  const [activeTab, setActiveTab] = useState(1);

  const handleTabClick = (tabNumber) => {
    setActiveTab(tabNumber);
  };

  return (
    <div className="App">
      <h1>Js </h1>
      <div className="tabs">
        <Tab
          label="Unminify js"
          active={activeTab === 1}
          onClick={() => handleTabClick(1)}
        />
        <Tab
          label="HTMl Stripper"
          active={activeTab === 2}
          onClick={() => handleTabClick(2)}
        />
        <Tab
          label=" Reverse gst calculator "
          active={activeTab === 3}
          onClick={() => handleTabClick(3)}
        />
      </div>
      <div className="tab-content">
        {activeTab === 1 && <JavaScriptUnminifier />}
        {activeTab === 2 && <HtmlStripper/>}
        {activeTab === 3 && <ReverseGSTCalculator/>}
      </div>
    </div>
  );
}

export default App;
