import React, { useState, useEffect } from 'react';

const ReverseGSTCalculator = ({ defaultGSTRate }) => {
  const [amountExcludingGST, setAmountExcludingGST] = useState('');
  const [gstRate, setGSTRate] = useState(defaultGSTRate || 18); // Default GST rate of 18%
  const [amountIncludingGST, setAmountIncludingGST] = useState('');
  const [totalGSTAmount, setTotalGSTAmount] = useState('');

  useEffect(() => {
    calculateGST();
  }, [amountExcludingGST, amountIncludingGST, gstRate]);

  const handleAmountIncludingGSTChange = (e) => {
    setAmountIncludingGST(e.target.value);
  };

  const handleAmountExcludingGSTChange = (e) => {
    setAmountExcludingGST(e.target.value);
  };

  const handleGSTRateChange = (e) => {
    setGSTRate(parseInt(e.target.value));
  };

  const calculateGST = () => {
    const total = parseFloat(amountIncludingGST);
    const excluding = parseFloat(amountExcludingGST);
    const rate = gstRate / 100;
    if (!isNaN(total) && isNaN(excluding)) {
      const excludingGST = total / (1 + rate);
      const gstAmount = total - excludingGST;
      setAmountExcludingGST(excludingGST.toFixed(2));
      setTotalGSTAmount(gstAmount.toFixed(2));
    } else if (isNaN(total) && !isNaN(excluding)) {
      const includingGST = excluding * (1 + rate);
      const gstAmount = includingGST - excluding;
      setAmountIncludingGST(includingGST.toFixed(2));
      setTotalGSTAmount(gstAmount.toFixed(2));
    } else {
      setAmountExcludingGST('');
      setAmountIncludingGST('');
      setTotalGSTAmount('');
    }
  };

  return (
    <div>
      <h1>Reverse GST Calculator</h1>
      <label>Amount Excluding GST:</label>
      <input
        type="number"
        value={amountExcludingGST}
        onChange={handleAmountExcludingGSTChange}
      />
      <label>GST Slab/Rate:</label>
      <input
        type="range" // Use type="range" for slider input
        min="0"
        max="100"
        value={gstRate}
        onChange={handleGSTRateChange}
      />
      <label>Amount Including GST:</label>
      <input
        type="number"
        value={amountIncludingGST}
        onChange={handleAmountIncludingGSTChange}
      />
      <span>{gstRate}%</span> {/* Display selected rate */}
      {totalGSTAmount && (
        <div>
          <p>Total GST Amount: â‚¹ {totalGSTAmount}</p>
        </div>
      )}
    </div>
  );
}

export default ReverseGSTCalculator;
